/* [수정] id 변경, resultMap 사용 */
SELECT inout.document_no, mm.material_code AS materialCode, mm.material_name AS materialDesc, inout.quantity, inout.plant_code AS plantCode, inout.mstorage_code AS stockLocation, inout.document_date, inout.document_type FROM ( SELECT mi.material_in_no AS document_no, mi.material_no, mi.material_in_cnt AS quantity, mi.plant_code, mi.mstorage_code, mi.material_in_date AS document_date, 'IN' AS document_type FROM chill.material_in mi /* [수정] 동적 조건 WHERE 절을 UNION ALL 바깥으로 이동 (가독성 및 일부 DB 최적화 고려) */
UNION ALL
SELECT mo.material_out_no AS document_no, mo.material_no, mo.material_out_cnt AS quantity, mo.plant_code, mo.mstorage_code, mo.material_out_date AS document_date, 'OUT' AS document_type FROM chill.material_out mo /* [수정] 동적 조건 WHERE 절을 UNION ALL 바깥으로 이동 */
) inout JOIN chill.material mm ON inout.material_no = mm.material_no <where> /* 기간 필터 */ inout.document_date >= #{startDate} AND inout.document_date < DATE_ADD(#{endDate}, INTERVAL 1 DAY) /* 자재 코드 필터 */ <if test = "materialCodeFrom != null and materialCodeFrom != ''"> AND mm.material_code >= #{materialCodeFrom} </if> <if test = "materialCodeTo != null and materialCodeTo != ''"> AND mm.material_code <= #{materialCodeTo} </if> /* 플랜트 필터 */ <if test = "plantCodeFrom != null and plantCodeFrom != ''"> AND inout.plant_code >= #{plantCodeFrom} </if> <if test = "plantCodeTo != null and plantCodeTo != ''"> AND inout.plant_code <= #{plantCodeTo} </if> /* 저장 위치 필터 */ <if test = "storageCodeFrom != null and storageCodeFrom != ''"> AND inout.mstorage_code >= #{storageCodeFrom} </if> <if test = "storageCodeTo != null and storageCodeTo != ''"> AND inout.mstorage_code <= #{storageCodeTo} </if> </where> ORDER BY inout.document_date, inout.document_type
