mapper.select:
WITH ranked_pm AS ( SELECT purc_order_no, progress_monitoring_date AS last_date, SUM(material_cnt) OVER(PARTITION BY purc_order_no) AS total_cnt, ROW_NUMBER() OVER(PARTITION BY purc_order_no ORDER BY progress_monitoring_date DESC) AS rn FROM progress_monitoring ), latest_pm AS ( SELECT purc_order_no, last_date, total_cnt FROM ranked_pm WHERE rn = 1 ) SELECT * FROM ( SELECT po.purc_order_code , po.purc_order_status , sup.sup_name , po.purc_order_reg_date , LEAST(DATE_ADD(pm.last_date, INTERVAL 7 DAY), mrp_due_date) AS next_progress_date , mrp.mrp_due_date , emp.emp_name , ROUND((IFNULL(pm.total_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) AS total_progress_rate , ROUND( (DATEDIFF(CURDATE(),po.purc_order_reg_date) * 1.0 / DATEDIFF(mrp.mrp_due_date, po.purc_order_reg_date)) * 100, 1) AS expected_progress_rate , CASE WHEN ROUND((IFNULL(pm.total_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) = 100 AND po.purc_order_status = 1 THEN 'done' WHEN ROUND((IFNULL(pm.total_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) >= ROUND((DATEDIFF(CURDATE(),po.purc_order_reg_date) * 1.0 / DATEDIFF(mrp.mrp_due_date, po.purc_order_reg_date)) * 100, 1) THEN 'R0' WHEN ROUND((DATEDIFF(CURDATE(),po.purc_order_reg_date) * 1.0 / DATEDIFF(mrp.mrp_due_date, po.purc_order_reg_date)) * 100, 1) - ROUND((IFNULL(pm.total_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) <= 15 THEN 'R1' WHEN ROUND((DATEDIFF(CURDATE(),po.purc_order_reg_date) * 1.0 / DATEDIFF(mrp.mrp_due_date, po.purc_order_reg_date)) * 100, 1) - ROUND((IFNULL(pm.total_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) <= 24 THEN 'R2' ELSE 'R3' END AS progress_eval FROM purchase_order po JOIN contract ct ON po.cont_no = ct.cont_no JOIN supplier sup ON ct.sup_no = sup.sup_no JOIN material_resource_plan mrp ON po.mrp_no = mrp.mrp_no JOIN employee emp ON po.emp_no = emp.emp_no
LEFT JOIN latest_pm pm ON pm.purc_order_no = po.purc_order_no ) AS base
base.purc_order_code LIKE CONCAT('%',#{keyword},'%')
base.sup_name LIKE CONCAT('%',#{keyword},'%')
base.purc_order_reg_date = #{keyword}
DATE(base.next_progress_date) = #{keyword}
base.mrp_due_date = #{keyword}
base.emp_name LIKE CONCAT('%',#{keyword},'%')
base.total_progress_rate = 0
base.total_progress_rate > 0 AND base.total_progress_rate < 100
base.total_progress_rate = 100
base.progress_eval = #{keyword}
ORDER BY po.purc_order_reg_date DESC LIMIT #{startRow}, #{pageSize}

SELECT COUNT(*) FROM progress_monitoring pm JOIN purchase_order po ON pm.purc_order_no = po.purc_order_no JOIN material ma ON pm.material_no = ma.material_no JOIN employee emp ON pm.emp_no = emp.emp_no JOIN contract ct ON po.cont_no = ct.cont_no JOIN supplier sup ON ct.sup_no = sup.sup_no JOIN material_resource_plan mrp ON po.mrp_no = mrp.mrp_no
po.purc_order_code LIKE CONCAT('%',#{keyword},'%')
sup.sup_name LIKE CONCAT('%',#{keyword},'%')
po.purc_order_reg_date = #{keyword}
DATE(LEAST(DATE_ADD(progress_monitoring_date, INTERVAL 7 DAY), mrp_due_date)) = #{keyword}
mrp.mrp_due_date = #{keyword}
emp.emp_name LIKE CONCAT('%',#{keyword},'%')
(ROUND((IFNULL(pm.material_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1)) LIKE CONCAT('%',#{keyword},'%')

WITH ranked_pm AS ( SELECT purc_order_no, progress_monitoring_date AS last_date, SUM(material_cnt) OVER(PARTITION BY purc_order_no) AS total_cnt, ROW_NUMBER() OVER(PARTITION BY purc_order_no ORDER BY progress_monitoring_date DESC) AS rn FROM progress_monitoring ), latest_pm AS ( SELECT purc_order_no, last_date, total_cnt FROM ranked_pm WHERE rn = 1 ) SELECT * FROM ( SELECT po.purc_order_code , sup.sup_name , po.purc_order_reg_date , LEAST(DATE_ADD(pm.last_date, INTERVAL 7 DAY), mrp_due_date) AS next_progress_date , mrp.mrp_due_date , emp.emp_name , ROUND((IFNULL(pm.total_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) AS total_progress_rate , ROUND( (DATEDIFF(CURDATE(),po.purc_order_reg_date) * 1.0 / DATEDIFF(mrp.mrp_due_date, po.purc_order_reg_date)) * 100, 1) AS expected_progress_rate , CASE WHEN ROUND((IFNULL(pm.total_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) >= ROUND((DATEDIFF(CURDATE(),po.purc_order_reg_date) * 1.0 / DATEDIFF(mrp.mrp_due_date, po.purc_order_reg_date)) * 100, 1) THEN 'R0' WHEN ROUND((DATEDIFF(CURDATE(),po.purc_order_reg_date) * 1.0 / DATEDIFF(mrp.mrp_due_date, po.purc_order_reg_date)) * 100, 1) - ROUND((IFNULL(pm.total_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) <= 15 THEN 'R1' WHEN ROUND((DATEDIFF(CURDATE(),po.purc_order_reg_date) * 1.0 / DATEDIFF(mrp.mrp_due_date, po.purc_order_reg_date)) * 100, 1) - ROUND((IFNULL(pm.total_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) <= 24 THEN 'R2' ELSE 'R3' END AS progress_eval FROM purchase_order po JOIN contract ct ON po.cont_no = ct.cont_no JOIN supplier sup ON ct.sup_no = sup.sup_no JOIN material_resource_plan mrp ON po.mrp_no = mrp.mrp_no JOIN employee emp ON po.emp_no = emp.emp_no
LEFT JOIN latest_pm pm ON pm.purc_order_no = po.purc_order_no ) AS base
base.purc_order_code LIKE CONCAT('%',#{keyword},'%')
base.sup_name LIKE CONCAT('%',#{keyword},'%')
base.purc_order_reg_date = #{keyword}
DATE(base.next_progress_date) = #{keyword}
base.mrp_due_date = #{keyword}
base.emp_name LIKE CONCAT('%',#{keyword},'%')
base.total_progress_rate = 0
base.total_progress_rate > 0 AND base.total_progress_rate < 100
base.total_progress_rate = 100
base.progress_eval = #{keyword}
ORDER BY po.purc_order_reg_date DESC

