mapper.select:
SELECT po.purc_order_code , sup.sup_name , po.purc_order_reg_date , LEAST(DATE_ADD(progress_monitoring_date, INTERVAL 7 DAY), mrp_due_date) AS next_progress_date , mrp.mrp_due_date , emp.emp_name , ROUND((IFNULL(pm.material_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) AS total_progress_rate FROM progress_monitoring pm JOIN purchase_order po ON pm.purchase_order_purc_order_no = po.purc_order_no JOIN material ma ON pm.material_material_no = ma.material_no JOIN employee emp ON pm.employee_emp_no = emp.emp_no JOIN contract ct ON po.contract_cont_no = ct.cont_no JOIN supplier sup ON ct.supplier_sup_no = sup.sup_no JOIN material_resource_plan mrp ON po.material_resource_plan_mrp_no = mrp.mrp_no
po.purc_order_code LIKE CONCAT('%',#{keyword},'%')
sup.sup_name LIKE CONCAT('%',#{keyword},'%')
po.purc_order_reg_date = #{keyword}
DATE(LEAST(DATE_ADD(progress_monitoring_date, INTERVAL 7 DAY), mrp_due_date)) = #{keyword}
mrp.mrp_due_date = #{keyword}
emp.emp_name LIKE CONCAT('%',#{keyword},'%')
IFNULL(pm.material_cnt,0) = 0
pm.material_cnt IS NOT NULL AND pm.material_cnt > 0 AND ROUND((IFNULL(pm.material_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) < 100
ROUND((IFNULL(pm.material_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) = 100

SELECT po.purc_order_code , sup.sup_name , po.purc_order_reg_date , LEAST(DATE_ADD(progress_monitoring_date, INTERVAL 7 DAY), mrp_due_date) AS next_progress_date , mrp.mrp_due_date , emp.emp_name , ROUND((IFNULL(pm.material_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) AS total_progress_rate FROM progress_monitoring pm JOIN purchase_order po ON pm.purchase_order_purc_order_no = po.purc_order_no JOIN material ma ON pm.material_material_no = ma.material_no JOIN employee emp ON pm.employee_emp_no = emp.emp_no JOIN contract ct ON po.contract_cont_no = ct.cont_no JOIN supplier sup ON ct.supplier_sup_no = sup.sup_no JOIN material_resource_plan mrp ON po.material_resource_plan_mrp_no = mrp.mrp_no
po.purc_order_code LIKE CONCAT('%',#{keyword},'%')
sup.sup_name LIKE CONCAT('%',#{keyword},'%')
po.purc_order_reg_date = #{keyword}
DATE(LEAST(DATE_ADD(progress_monitoring_date, INTERVAL 7 DAY), mrp_due_date)) = #{keyword}
mrp.mrp_due_date = #{keyword}
emp.emp_name LIKE CONCAT('%',#{keyword},'%')
IFNULL(pm.material_cnt,0) = 0
pm.material_cnt IS NOT NULL AND pm.material_cnt > 0 AND ROUND((IFNULL(pm.material_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) < 100
ROUND((IFNULL(pm.material_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1) = 100
LIMIT #{startRow}, #{pageSize}

SELECT COUNT(*) FROM progress_monitoring pm JOIN purchase_order po ON pm.purchase_order_purc_order_no = po.purc_order_no JOIN material ma ON pm.material_material_no = ma.material_no JOIN employee emp ON pm.employee_emp_no = emp.emp_no JOIN contract ct ON po.contract_cont_no = ct.cont_no JOIN supplier sup ON ct.supplier_sup_no = sup.sup_no JOIN material_resource_plan mrp ON po.material_resource_plan_mrp_no = mrp.mrp_no
po.purc_order_code LIKE CONCAT('%',#{keyword},'%')
sup.sup_name LIKE CONCAT('%',#{keyword},'%')
po.purc_order_reg_date = #{keyword}
DATE(LEAST(DATE_ADD(progress_monitoring_date, INTERVAL 7 DAY), mrp_due_date)) = #{keyword}
mrp.mrp_due_date = #{keyword}
emp.emp_name LIKE CONCAT('%',#{keyword},'%')
(ROUND((IFNULL(pm.material_cnt,0) * 1.0 / ct.cont_material_cnt) * 100, 1)) LIKE CONCAT('%',#{keyword},'%')

SELECT po.purc_order_no , po.purc_order_code , po.purc_order_reg_date , emp.emp_no , emp.emp_name , sup.sup_name , sup.sup_reg_no , sup.sup_ceo_name , sup.sup_phone , CONCAT(sup.sup_addr1, sup.sup_addr2) AS sup_total_addr , sup.sup_cip , sup.sup_cip_phone , sup.sup_cip_email , ct.cont_material_cnt
FROM progress_monitoring pm JOIN purchase_order po ON pm.purchase_order_purc_order_no = po.purc_order_no JOIN employee emp ON pm.employee_emp_no = emp.emp_no JOIN contract ct ON po.contract_cont_no = ct.cont_no JOIN supplier sup ON ct.supplier_sup_no = sup.sup_no
WHERE po.purc_order_code = #{purc_order_code}

mapper.insert:
INSERT INTO progress_monitoring (
	progress_monitoring_code
	, progress_monitoring_ver
	, material_cnt
	, progress_monitoring_date
	, progress_monitoring_etc
	, purchase_order_purc_order_no
	, material_material_no
	, employee_emp_no
)
VALUES (
	#{progress_monitoring_code}
	, #{progress_monitoring_ver}
	, #{material_cnt}
	, #{progress_monitoring_date}
	, #{progress_monitoring_etc}
	, #{purc_order_no}
	, #{material_no}
	, #{emp_no}
)
