mapper.select:
select quotation.quot_no as quotNo, quotation.quot_code as quotCode, quotation.sup_no as supNo, quotation.quot_file as quotFile, quotation.quot_dterms as quotDterms, quotation.quot_pterms as quotPterms, quotation.material_no as materialNo, quotation.quot_price as quotPrice, quotation.quot_price_currency as quotPriceCurrency, quotation.quot_material_cnt as quotMaterialCnt, quotation.quot_material_sku as quotMaterialSku, quotation.quot_due_date as quotDueDate, quotation.quot_etc as quotEtc,
supplier.sup_name as supName, supplier.sup_reg_no as supRegNo,
material.material_name as materialName, material.material_code as materialCode, material.material_raw_material as materialRawMaterial, material.material_description as materialDescription, material.material_unit as materialUnit from quotation join supplier on quotation.sup_no = supplier.sup_no join material on quotation.material_no = material.material_no where quot_no=#{quotNo}

mapper.update:
update quotation 
set quot_code=#{quotCode}, sup_no=#{supNo}, quot_file=#{quotFile}, quot_dterms=#{quotDterms}, quot_pterms=#{quotPterms}, 
material_no=#{materialNo}, quot_price=#{quotPrice}, quot_price_currency=#{quotPriceCurrency}, quot_material_cnt=#{quotMaterialCnt}, 
quot_material_sku=#{quotMaterialSku}, quot_due_date=#{quotDueDate}, quot_etc=#{quotEtc}, quot_state=#{quotState} 
where quot_no=#{quotNo}

update quotation
set quot_state = quot_state + 1
where quot_no=#{quotNo}
