mapper.select:
SELECT emp_id AS user_id, emp_name AS user_name, 'EMP' AS user_type FROM employee WHERE emp_id = #{user_id} AND emp_password = #{user_password}
UNION ALL
SELECT sup_id AS user_id, sup_name AS user_name, 'SUP' AS user_type FROM supplier WHERE sup_id = #{user_id} AND sup_password = #{user_password}

SELECT emp_no
FROM employee
WHERE emp_id = #{user_id}

SELECT COUNT(*)
FROM employee
WHERE emp_id = #{emp_id}

mapper.update:
UPDATE employee
SET emp_job = #{emp_job}
	, emp_id = #{emp_id}
	, emp_password = #{emp_password}
	, emp_phone = #{emp_phone}
	, emp_email = #{emp_email}
WHERE emp_no = #{emp_no}
